import "./App.css";
import React, { Component } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import HomePage from "./componets/HomePage/HomePage";
import axios from "axios";

class App extends Component {
  constructor() {
    super();
    this.state = {
      searchCity: "",
      filteredCities: [],
    };
  }

  filterCities = (searchInput) => {
    this.setState(
      {
        searchCity: searchInput,
      },
      () => {
        const searchCity = this.state.searchCity;
        let citiesInfoArray = [...this.state.citiesInfo];
        citiesInfoArray = citiesInfoArray.filter((city) => {
          return city.CityDetails.FullName.toLowerCase().includes(searchCity);
        });
        this.setState({
          filteredCities: citiesInfoArray,
        });
      }
    );
  };
  componentDidMount() {
    this.setState({ citiesInfo: axios.get("http://localhost:4000") }, () => {
      console.log(this.state);
    });
  }
  render() {
    return (
      <div>
        <Router>
          <div>
            <Route
              exact
              path="/"
              render={() => (
                <HomePage
                  citiesInfo={
                    this.state.searchCity === ""
                      ? this.state.citiesInfo
                      : this.state.filteredCities
                  }
                  filterCities={this.filterCities}
                />
              )}
            />
          </div>
        </Router>
      </div>
    );
  }
}
export default App;
