import "./App.css";
import React, { Component } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import HomePage from "./componets/HomePage/HomePage";
import axios from "axios";

class App extends Component {
  constructor() {
    super();
    this.state = {
      searchCity: "",
      filteredCities: [],
    };
    axios.get("http://localhost:4000").then((RESULT) => {
      this.setState({ citiesInfo: RESULT.data });
    });
  }

  filterCities = (searchInput) => {
    this.setState(
      {
        searchCity: searchInput,
      },
      () => {
        const searchCity = this.state.searchCity;
        let citiesInfoArray = [...this.state.citiesInfo];
        citiesInfoArray = citiesInfoArray.filter((city) => {
          return city.CityDetails.FullName.toLowerCase().includes(searchCity);
        });
        this.setState({
          filteredCities: citiesInfoArray,
        });
      }
    );
  };
  componentDidUpdate() {
    console.log(this.state.citiesInfo);
  }
  renderAfterFitchData() {
    if (this.state.citiesInfo) {
      return true;
    } else {
      return false;
    }
  }
  render() {
    return (
      <div>
        {this.renderAfterFitchData() ? (
          <div>
            {" "}
            {this.state.citiesInfo ? (
              <Router>
                <div>
                  <Route
                    exact
                    path="/"
                    render={() => (
                      <HomePage
                        citiesInfo={
                          this.state.searchCity === ""
                            ? this.state.citiesInfo
                            : this.state.filteredCities
                        }
                        filterCities={this.filterCities}
                      />
                    )}
                  />
                </div>
              </Router>
            ) : (
              <div> </div>
            )}
          </div>
        ) : (
          <h1>Loading.....</h1>
        )}
      </div>
    );
  }
}
export default App;
