import "./App.css";
import React, { Component } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import HomePage from "./componets/HomePage/HomePage";
import City from "./componets/City/City";
import axios from "axios";

class App extends Component {
  constructor() {
    super();
    this.state = {
      searchCity: "",
      continent: "",
      filteredCities: [],
      pickedCity: null,
    };
    axios.get("http://localhost:4000").then((RESULT) => {
      this.setState({ citiesInfo: RESULT.data });
    });
  }

  filterCities = (searchInput) => {
    if (searchInput) {
      this.setState({ searchCity: searchInput }, () => {
        console.log(this.state.searchCity);
        axios
          .get(`http://localhost:4000/country/${this.state.searchCity}`)
          .then((RESULT) => {
            if (RESULT) {
              this.setState({ citiesInfo: RESULT.data });
            }
          });
      });
    } else {
      axios.get("http://localhost:4000").then((RESULT) => {
        this.setState({ citiesInfo: RESULT.data });
      });
    }
  };
  filterContinent = (continent) => {
    this.setState({ continent: continent }, () => {
      axios
        .get(
          `http://localhost:4000/continent/${this.state.continent}?${this.state.searchCity}`
        )
        .then((RESULT) => {
          this.setState({ citiesInfo: RESULT.data });
        });
    });
  };

  specificCity = (full_name) => {
    axios.get(`http://localhost:4000/country/${full_name}`).then((RESULT) => {
      this.setState({ pickedCity: RESULT });
    });
  };

  renderAfterFitchData() {
    if (this.state.citiesInfo) {
      return true;
    } else {
      return false;
    }
  }
  render() {
    return (
      <div>
        {this.renderAfterFitchData() ? (
          <div>
            {
              <Router>
                <div>
                  <Route
                    exact
                    path="/"
                    render={() => (
                      <HomePage
                        citiesInfo={this.state.citiesInfo}
                        searchCity={this.state.searchCity}
                        filteredCities={this.filterCities}
                        filterContinent={this.filterContinent}
                      />
                    )}
                  />
                  <Route
                    exact
                    path="/city/:cityName"
                    render={({ match }) => {
                      return (
                        <City
                          match={match}
                          specificCity={this.specificCity}
                          pickedCity={this.state.pickedCity}
                        />
                      );
                    }}
                  />
                </div>
              </Router>
            }
          </div>
        ) : (
          <h1>Loading.....</h1>
        )}
      </div>
    );
  }
}
export default App;
