import axios from "axios";
import { Component } from "react";
import moment from "moment";
import { Link } from "react-router-dom";
export default class Joined extends Component {
  constructor() {
    super();
    this.state = {};
  }
  componentDidMount = () => {
    axios.get("http://localhost:4000/SesstionJoinedPlans").then((RESULT) => {
      this.setState({ visitedPlans: RESULT.data });
    });
  };
  displayUser = (arrayOfPlans) => {
    return (
      <div className="HomeUser">
        <div className="userInfo">
          {!this.props.noBack ? (
            <Link className="noPlans" to="/listOfUsers">
              Go Back
            </Link>
          ) : (
            <Link className="noPlans" to="/Plans">
              Retrun
            </Link>
          )}
          <img className="UserPicture" src={this.state.user.picture} />
        </div>
        {this.state.user.Plans.length != 0 ? (
          this.displayUserPlan()
        ) : (
          <div className="noPlans">
            <h1>{this.state.user.userName} not having plans yet</h1>
          </div>
        )}
      </div>
    );
  };
  render() {
    console.log(this.state.visitedPlans);
    return this.state.visitedPlans ? (
      this.displayUser(visitedPlans)
    ) : (
      <h1>No Joined Plans for this user</h1>
    );
  }
}
